import java.util.*;class FractionMarks{   public static void main(String[] args)   {      Scanner kbReader = new Scanner(System.in);             Fraction mark = new Fraction();            System.out.println("Enter num for mark(Enter only a negative num to end)");      mark.num = kbReader.nextInt();             System.out.println("Enter den for mark(Enter only a negative den to end)");      mark.setDen(kbReader.nextInt());            Fraction max = new Fraction();      Fraction min = new Fraction();      Fraction average = new Fraction();      int numFractions = 0;            while(mark.toDecimal() > 0){         numFractions++;                  if(mark.toDecimal() > max.toDecimal()){            max.num = mark.num;            max.setDen(mark.getDen());         }                  if(mark.toDecimal() < min.toDecimal()){            min.num = mark.num;            min.setDen(mark.getDen());         }                  average.plusEquals(mark);                  System.out.println("Enter num for mark(Enter only a negative num to end)");         mark.num = kbReader.nextInt();                System.out.println("Enter den for mark(Enter only a negative den to end)");         mark.setDen(kbReader.nextInt());      }             average.setDen(average.getDen() * numFractions);      System.out.println("Max Mark: " + max);      System.out.println("Min Mark: " + min);      System.out.println("Average Mark: " + average);   }}